import { Component, OnInit } from '@angular/core';

@Component({
  selector: 'app-new-rating',
  templateUrl: './new-rating.component.html',
  styleUrls: ['./new-rating.component.css']
})
export class NewRatingComponent implements OnInit {

  isTrasporto: boolean = false;
  isCarne: boolean = false;
  isLatte: boolean = false;
  isTableVisible: boolean = false;
  choosen: boolean = false;

  trasportoTrue(){
    this.isTrasporto = true;
    this.choosen = true;
  }

  carneTrue(){
    this.isCarne = true;
    this.choosen = true;
  }

  latteTrue(){
    this.isLatte = true;
    this.choosen = true;
  }

  questions: RatingQuestion[] = [
    {
      id: 1,
      riferimento: 'Reg. 852/04, All II, Cap I, Punto 1',
      requisiti: 'Le strutture destinate agli alimenti sono tenute pulite, sottoposte a manutenzione e tenute in buone condizioni?'
    },
    //... altre domande
  ];

  constructor() { }

  ngOnInit(): void {
  }

  submitRatings() {
    // Qui puoi gestire l'invio delle risposte, ad esempio salvandole in un database
    console.log(this.questions);
  }

}

export interface RatingQuestion {
  id: number;
  riferimento: string;
  requisiti: string;
  risposta?: string | number; // per gestire sia stringhe che numeri come risposte
}
