import { Component, OnInit } from '@angular/core';

export enum RatingType {
  CARNE = 'carne',
  LATTE = 'latte',
  TRASPORTO = 'trasporto'
}

@Component({
  selector: 'app-new-rating',
  templateUrl: './new-rating.component.html',
  styleUrls: ['./new-rating.component.css']
})
export class NewRatingComponent implements OnInit {
  questions: RatingQuestion[] = [];
  currentRatingType?: RatingType;
  RatingType = RatingType;

  carneOptions = ['Sì', 'No', 'N.A.', 'N.V.', 'N.N.', 'N.P.'];
  latteOptions = ['Sì', 'No', 'N.A.', 'N.V.', 'N.N.', 'N.P.'];
  trasportoOptions = [1, 2, 3, 4, 5, 6];

  constructor() { }

  ngOnInit(): void {
  }

  setRatingType(type: RatingType) {
    this.currentRatingType = type;
    // In questa sezione puoi inizializzare le domande in base al tipo di rating
  }

  submitRatings() {
    // Qui gestisci l'invio delle risposte al tuo backend o servizio
  }
}

export interface RatingQuestion {
  id: number;
  riferimento: string;
  requisiti: string;
  risposta?: string | number; // per gestire sia stringhe che numeri come risposte
}
