import { Component, OnInit } from '@angular/core';

@Component({
  selector: 'app-new-rating',
  templateUrl: './new-rating.component.html',
  styleUrls: ['./new-rating.component.css']
})
export class NewRatingComponent implements OnInit {
  questions: RatingQuestion[] = [
    {
      id: 1,
      riferimento: 'Reg. 852/04, All II, Cap I, Punto 1',
      requisiti: 'Le strutture destinate agli alimenti sono tenute pulite, sottoposte a manutenzione e tenute in buone condizioni?'
    },
    //... altre domande
  ];

  constructor() { }

  ngOnInit(): void {
  }

  submitRatings() {
    // Qui puoi gestire l'invio delle risposte, ad esempio salvandole in un database
    console.log(this.questions);
  }

}

export interface RatingQuestion {
  id: number;
  riferimento: string;
  requisiti: string;
  risposta?: 'Sì' | 'No' | 'N.A.' | 'N.V.' | 'N.N.' | 'N.P.';
}
